import { Component, OnInit } from '@angular/core';
import { Router, ActivatedRoute, Params } from '@angular/router';

import { DataService } from '../core/data.service';
import { ICustomer, IOrder, IOrderItem } from '../shared/interfaces';

@Component({
  selector: 'app-orders',
  templateUrl: './orders.component.html',
  styleUrls: ['./orders.component.css'],
})

export class OrdersComponent implements OnInit {
  orders: IOrder[] = [];
  customer: ICustomer | null = null;

  constructor(
    private dataService: DataService,
    private route: ActivatedRoute
  ) {}

  ngOnInit() {
    const id = Number(this.route.snapshot.paramMap.get('id'));
    this.dataService.getOrders(id).subscribe((orders: IOrder[]) => {
      this.orders = orders;
    });
    this.dataService.getCustomer(id).subscribe((customer: ICustomer | null) => {
      this.customer = customer;
    });
  }
}
<div *ngIf="customer">
  <h1>Orders for {{ customer.name | capitalize }}</h1>

  <table class="table table-hover orders-table" *ngFor="let order of orders">
    <tr *ngFor="let orderItem of order.orderItems">
      <td>{{ orderItem.productName }}</td>
      <td>{{ orderItem.itemCost | currency : "USD" : "symbol" }}</td>
    </tr>
  </table>
</div>
<div *ngIf="!customer">No customer found</div>
<br />
<a routerLink="/customers">View All Customers</a>
